import sjcl from 'sjcl';
import {InternalUtils} from './utils.js';

/**
 * Node.js Crypto module.
 * @private
 * @type {Object}
 */
const NodeCrypto = InternalUtils.require('crypto');

/**
 * An object containing some cryptography functions
 * with dirty workarounds for Node.js and browsers.
 * @private
 * @type {Object}
 */
export const Crypto = {};

if (NodeCrypto) {
	let bufferFrom;

	if (typeof Buffer.from === 'function') {
		bufferFrom = Buffer.from;
	} else {
		// Node.js < 5.10.0
		bufferFrom = function (arrbuf) {
			// eslint-disable-next-line unicorn/no-new-buffer
			const nodeBuf = new Buffer(arrbuf.byteLength);
			const arr = new Uint8Array(arrbuf);

			for (let i = 0; i < arr.length; i++) {
				nodeBuf[i] = arr[i];
			}

			return nodeBuf;
		};
	}

	let bufferTo;

	if (Buffer.prototype instanceof Uint8Array) {
		bufferTo = function (nodeBuf) {
			return nodeBuf;
		};
	} else {
		// Node.js < 4.0.0
		bufferTo = function (nodeBuf) {
			const arr = new Uint8Array(nodeBuf.length);

			for (let i = 0; i < arr.length; i++) {
				arr[i] = nodeBuf[i];
			}

			return arr;
		};
	}

	Crypto.randomBytes = function (size) {
		return bufferTo(NodeCrypto.randomBytes(size));
	};

	// In Node.js, the command:
	//   $ openssl list-message-digest-algorithms
	// displays the available digest algorithms.
	Crypto.hmacDigest = function (algorithm, key, message) {
		return bufferTo(
			NodeCrypto.createHmac(algorithm, bufferFrom(key))
				.update(bufferFrom(message))
				.digest()
		);
	};
} else {
	let getRandomValues;

	if (typeof global.crypto !== 'undefined' && typeof global.crypto.getRandomValues === 'function') {
		getRandomValues = function (arr) {
			global.crypto.getRandomValues(arr);
		};
	} else if (typeof global.msCrypto !== 'undefined' && typeof global.msCrypto.getRandomValues === 'function') {
		getRandomValues = function (arr) {
			global.msCrypto.getRandomValues(arr);
		};
	} else {
		console.warn('Cryptography API not available, falling back to \'Math.random\'...');
		getRandomValues = function (arr) {
			for (let i = 0; i < arr.length; i++) {
				arr[i] = Math.floor(Math.random() * 256);
			}
		};
	}

	Crypto.randomBytes = function (size) {
		const arr = new Uint8Array(size);
		getRandomValues(arr);
		return arr;
	};

	Crypto.hmacDigest = function (algorithm, key, message) {
		const hash = sjcl.hash[algorithm.toLowerCase()];
		if (typeof hash === 'undefined') {
			throw new TypeError('Unknown hash function');
		}

		// eslint-disable-next-line new-cap
		const hmac = new sjcl.misc.hmac(sjcl.codec.arrayBuffer.toBits(key), hash);
		hmac.update(sjcl.codec.arrayBuffer.toBits(message));

		return sjcl.codec.arrayBuffer.fromBits(hmac.digest(), false);
	};
}

/**
 * One Time Password (HOTP/TOTP) library for Node.js and browser.
 * @module OTPAuth
 * @author Héctor Molinero Fernández <hector@molinero.xyz>
 */
export {HOTP, TOTP} from './otp.js';
export {URI} from './uri.js';
export {Secret} from './secret.js';
export {Utils} from './utils.js';

/**
 * Library version.
 * @type {string}
 */
export const version = process.env.VERSION;

import {Utils} from './utils.js';
import {Crypto} from './crypto.js';
import {Secret} from './secret.js';
import {URI} from './uri.js';

/**
 * Default configuration.
 * @private
 * @type {Object}
 */
const DC = {
	issuer: '',
	label: 'OTPAuth',
	algorithm: 'SHA1',
	digits: 6,
	counter: 0,
	period: 30,
	window: 50,
	pad: true
};

/**
 * HOTP: An HMAC-based One-time Password Algorithm (RFC 4226).
 * @see https://tools.ietf.org/html/rfc4226
 */
export class HOTP {
	/**
	 * @param {Object} [config] Configuration options.
	 * @param {string} [config.issuer=''] Account provider.
	 * @param {string} [config.label='OTPAuth'] Account label.
	 * @param {Secret} [config.secret=Secret] Secret key.
	 * @param {string} [config.algorithm='SHA1'] HMAC hashing algorithm.
	 * @param {number} [config.digits=6] Token length.
	 * @param {number} [config.counter=0] Initial counter value.
	 */
	constructor({issuer = DC.issuer, label = DC.label, secret = new Secret(), algorithm = DC.algorithm, digits = DC.digits, counter = DC.counter} = {}) {
		/**
		 * Account provider.
		 * @type {string}
		 */
		this.issuer = issuer;
		/**
		 * Account label.
		 * @type {string}
		 */
		this.label = label;
		/**
		 * Secret key.
		 * @type {Secret}
		 */
		this.secret = secret;
		/**
		 * HMAC hashing algorithm.
		 * @type {string}
		 */
		this.algorithm = algorithm;
		/**
		 * Token length.
		 * @type {number}
		 */
		this.digits = digits;
		/**
		 * Initial counter value.
		 * @type {number}
		 */
		this.counter = counter;
	}

	/**
	 * Generates an HOTP token.
	 * @param {Object} config Configuration options.
	 * @param {Secret} config.secret Secret key.
	 * @param {string} [config.algorithm='SHA1'] HMAC hashing algorithm.
	 * @param {number} [config.digits=6] Token length.
	 * @param {number} [config.counter=0] Counter value.
	 * @param {boolean} [config.pad=true] Add leading zeros to result.
	 * @returns {string|number} Token.
	 */
	static generate({secret, algorithm = DC.algorithm, digits = DC.digits, counter = DC.counter, pad = DC.pad}) {
		const digest = new Uint8Array(Crypto.hmacDigest(algorithm, secret.buffer, Utils.uint.encode(counter)));

		const offset = digest[digest.byteLength - 1] & 15;
		const binary = (
			((digest[offset] & 127) << 24) |
			((digest[offset + 1] & 255) << 16) |
			((digest[offset + 2] & 255) << 8) |
			(digest[offset + 3] & 255)
		) % Math.pow(10, digits);

		return pad
			? Array(1 + digits - String(binary).length).join('0') + binary
			: binary;
	}

	/**
	 * Generates an HOTP token.
	 * @param {Object} [config] Configuration options.
	 * @param {number} [config.counter=this.counter++] Counter value.
	 * @param {boolean} [config.pad=true] Add leading zeros to result.
	 * @returns {string|number} Token.
	 */
	generate({counter = this.counter++, pad} = {}) {
		return HOTP.generate({
			secret: this.secret,
			algorithm: this.algorithm,
			digits: this.digits,
			counter: counter,
			pad: pad
		});
	}

	/**
	 * Validates an HOTP token.
	 * @param {Object} config Configuration options.
	 * @param {string} config.token Token value.
	 * @param {Secret} config.secret Secret key.
	 * @param {string} [config.algorithm='SHA1'] HMAC hashing algorithm.
	 * @param {number} [config.counter=0] Counter value.
	 * @param {number} [config.window=50] Window of counter values to test.
	 * @returns {number|null} Token delta, or null if the token is not found.
	 */
	static validate({token, secret, algorithm, counter = DC.counter, window = DC.window}) {
		const searchToken = parseInt(token, 10);

		for (let i = counter - window; i <= counter + window; ++i) {
			const generatedToken = HOTP.generate({
				secret: secret,
				algorithm: algorithm,
				counter: i,
				digits: token.length,
				pad: false
			});

			if (searchToken === generatedToken) {
				return i - counter;
			}
		}

		return null;
	}

	/**
	 * Validates an HOTP token.
	 * @param {Object} config Configuration options.
	 * @param {string} config.token Token value.
	 * @param {number} [config.counter=this.counter] Counter value.
	 * @param {number} [config.window=50] Window of counter values to test.
	 * @returns {number|null} Token delta, or null if the token is not found.
	 */
	validate({token, counter = this.counter, window}) {
		return HOTP.validate({
			token: token,
			secret: this.secret,
			algorithm: this.algorithm,
			counter: counter,
			window: window
		});
	}

	/**
	 * Returns a Google Authenticator key URI.
	 * @returns {string} URI.
	 */
	toString() {
		return URI.stringify(this);
	}
}

/**
 * TOTP: Time-Based One-Time Password Algorithm (RFC 6238).
 * @see https://tools.ietf.org/html/rfc6238
 */
export class TOTP {
	/**
	 * @param {Object} [config] Configuration options.
	 * @param {string} [config.issuer=''] Account provider.
	 * @param {string} [config.label='OTPAuth'] Account label.
	 * @param {Secret} [config.secret=Secret] Secret key.
	 * @param {string} [config.algorithm='SHA1'] HMAC hashing algorithm.
	 * @param {number} [config.digits=6] Token length.
	 * @param {number} [config.period=30] Token time-step duration.
	 */
	constructor({issuer = DC.issuer, label = DC.label, secret = new Secret(), algorithm = DC.algorithm, digits = DC.digits, period = DC.period} = {}) {
		/**
		 * Account provider.
		 * @type {string}
		 */
		this.issuer = issuer;
		/**
		 * Account label.
		 * @type {string}
		 */
		this.label = label;
		/**
		 * Secret key.
		 * @type {string}
		 */
		this.secret = secret;
		/**
		 * HMAC hashing algorithm.
		 * @type {Secret}
		 */
		this.algorithm = algorithm;
		/**
		 * Token length.
		 * @type {number}
		 */
		this.digits = digits;
		/**
		 * Token time-step duration.
		 * @type {number}
		 */
		this.period = period;
	}

	/**
	 * Generates a TOTP token.
	 * @param {Object} config Configuration options.
	 * @param {Secret} config.secret Secret key.
	 * @param {string} [config.algorithm='SHA1'] HMAC hashing algorithm.
	 * @param {number} [config.digits=6] Token length.
	 * @param {number} [config.period=30] Token time-step duration.
	 * @param {number} [config.timestamp=Date.now] Timestamp value in milliseconds.
	 * @param {boolean} [config.pad=true] Add leading zeros to result.
	 * @returns {string|number} Token.
	 */
	static generate({secret, algorithm, digits, period = DC.period, timestamp = Date.now(), pad}) {
		return HOTP.generate({
			secret: secret,
			algorithm: algorithm,
			digits: digits,
			counter: Math.floor(timestamp / 1000 / period),
			pad: pad
		});
	}

	/**
	 * Generates a TOTP token.
	 * @param {Object} [config] Configuration options.
	 * @param {number} [config.timestamp=Date.now] Timestamp value in milliseconds.
	 * @param {boolean} [config.pad=true] Add leading zeros to result.
	 * @returns {string|number} Token.
	 */
	generate({timestamp = Date.now(), pad} = {}) {
		return TOTP.generate({
			secret: this.secret,
			algorithm: this.algorithm,
			digits: this.digits,
			period: this.period,
			timestamp: timestamp,
			pad: pad
		});
	}

	/**
	 * Validates a TOTP token.
	 * @param {Object} config Configuration options.
	 * @param {string} config.token Token value.
	 * @param {Secret} config.secret Secret key.
	 * @param {string} [config.algorithm='SHA1'] HMAC hashing algorithm.
	 * @param {number} [config.period=30] Token time-step duration.
	 * @param {number} [config.timestamp=Date.now] Timestamp value in milliseconds.
	 * @param {number} [config.window=50] Window of counter values to test.
	 * @returns {number|null} Token delta, or null if the token is not found.
	 */
	static validate({token, secret, algorithm, period = DC.period, timestamp = Date.now(), window}) {
		return HOTP.validate({
			token: token,
			secret: secret,
			algorithm: algorithm,
			counter: Math.floor(timestamp / 1000 / period),
			window: window
		});
	}

	/**
	 * Validates a TOTP token.
	 * @param {Object} config Configuration options.
	 * @param {string} config.token Token value.
	 * @param {number} [config.timestamp=Date.now] Timestamp value in milliseconds.
	 * @param {number} [config.window=50] Window of counter values to test.
	 * @returns {number|null} Token delta, or null if the token is not found.
	 */
	validate({token, timestamp, window}) {
		return TOTP.validate({
			token: token,
			secret: this.secret,
			algorithm: this.algorithm,
			period: this.period,
			timestamp: timestamp,
			window: window
		});
	}

	/**
	 * Returns a Google Authenticator key URI.
	 * @returns {string} URI.
	 */
	toString() {
		return URI.stringify(this);
	}
}

import {Utils} from './utils.js';
import {Crypto} from './crypto.js';

/**
 * Secret key object.
 */
export class Secret {
	/**
	 * @param {Object} [config] Configuration options.
	 * @param {ArrayBuffer} [config.buffer=Crypto.randomBytes] Secret key.
	 * @param {number} [config.size=20] Number of random bytes to generate, ignored if 'buffer' is provided.
	 */
	constructor({buffer, size = 20} = {}) {
		/**
		 * Secret key.
		 * @type {ArrayBuffer}
		 */
		this.buffer = typeof buffer === 'undefined'
			? Crypto.randomBytes(size).buffer
			: buffer;
	}

	/**
	 * Converts a raw string to a Secret object.
	 * @method fromRaw
	 * @param {string} str Raw string.
	 * @returns {Secret} Secret object.
	 */
	static fromRaw(str) {
		return new this({buffer: Utils.raw.encode(str)});
	}

	/**
	 * Converts a base32 string to a Secret object.
	 * @method fromB32
	 * @param {string} str Base32 string.
	 * @returns {Secret} Secret object.
	 */
	static fromB32(str) {
		return new this({buffer: Utils.b32.encode(str)});
	}

	/**
	 * Converts a hexadecimal string to a Secret object.
	 * @method fromHex
	 * @param {string} str Hexadecimal string.
	 * @returns {Secret} Secret object.
	 */
	static fromHex(str) {
		return new this({buffer: Utils.hex.encode(str)});
	}

	/**
	 * String representation of secret key.
	 * @type {string}
	 */
	get raw() {
		Object.defineProperty(this, 'raw', {
			enumerable: true,
			configurable: true,
			writable: true,
			value: Utils.raw.decode(this.buffer)
		});

		return this.raw;
	}

	/**
	 * Base32 representation of secret key.
	 * @type {string}
	 */
	get b32() {
		Object.defineProperty(this, 'b32', {
			enumerable: true,
			configurable: true,
			writable: true,
			value: Utils.b32.decode(this.buffer)
		});

		return this.b32;
	}

	/**
	 * Hexadecimal representation of secret key.
	 * @type {string}
	 */
	get hex() {
		Object.defineProperty(this, 'hex', {
			enumerable: true,
			configurable: true,
			writable: true,
			value: Utils.hex.decode(this.buffer)
		});

		return this.hex;
	}
}

import {Utils} from './utils.js';
import {Secret} from './secret.js';
import {HOTP, TOTP} from './otp.js';

/**
 * Valid key URI parameters.
 * @private
 * @type {Array}
 */
const OTPURI_PARAMS = ['issuer', 'label', 'secret', 'algorithm', 'digits', 'counter', 'period'];

/**
 * Key URI regex.
 *   otpauth://TYPE/[ISSUER:]LABEL?PARAMETERS
 * @private
 * @type {RegExp}
 */
const OTPURI_REGEX = RegExp(`^otpauth:\\/\\/([ht]otp)\\/(.+)\\?((?:&?(?:${OTPURI_PARAMS.join('|')})=[^&]+)+)$`, 'i');

/**
 * RFC 4648 base32 alphabet without pad.
 * @private
 * @type {string}
 */
const SECRET_REGEX = /^[2-7A-Z]+$/i;

/**
 * Regex for supported algorithms.
 * @private
 * @type {RegExp}
 */
const ALGORITHM_REGEX = /^SHA(?:1|256|512)$/i;

/**
 * Integer regex.
 * @private
 * @type {RegExp}
 */
const INTEGER_REGEX = /^[+-]?[0-9]+$/;

/**
 * Positive integer regex.
 * @private
 * @type {RegExp}
 */
const POSITIVE_INTEGER_REGEX = /^\+?[1-9][0-9]*$/;

/**
 * HOTP/TOTP object/string conversion.
 * @see https://github.com/google/google-authenticator/wiki/Key-Uri-Format
 */
export class URI {
	/**
	 * Parses a Google Authenticator key URI and returns an HOTP/TOTP object.
	 * @method parse
	 * @param {string} uri Google Authenticator Key URI.
	 * @returns {Object} HOTP/TOTP object.
	 */
	static parse(uri) {
		let uriGroups;

		try {
			uriGroups = decodeURIComponent(uri).match(OTPURI_REGEX);
		} catch (err) {}

		if (!Array.isArray(uriGroups)) {
			throw new URIError('Invalid URI format');
		}

		// Extract URI groups
		const uriType = uriGroups[1].toLowerCase();
		const uriLabel = uriGroups[2].split(/:(.+)/, 2);
		const uriParams = uriGroups[3].split('&').reduce(function (acc, cur) {
			const pairArr = cur.split(/=(.+)/, 2);
			const pairKey = pairArr[0].toLowerCase();
			const pairVal = pairArr[1];
			const pairAcc = acc;

			pairAcc[pairKey] = pairVal;
			return pairAcc;
		}, {});

		// 'OTP' will be instantiated with 'config' argument
		let OTP;
		let config = {};

		if (uriType === 'hotp') {
			OTP = HOTP;

			// Counter: required
			if (typeof uriParams.counter !== 'undefined' && INTEGER_REGEX.test(uriParams.counter)) {
				config.counter = parseInt(uriParams.counter, 10);
			} else {
				throw new TypeError('Missing or invalid \'counter\' parameter');
			}
		} else if (uriType === 'totp') {
			OTP = TOTP;

			// Period: optional
			if (typeof uriParams.period !== 'undefined') {
				if (POSITIVE_INTEGER_REGEX.test(uriParams.period)) {
					config.period = parseInt(uriParams.period, 10);
				} else {
					throw new TypeError('Invalid \'period\' parameter');
				}
			}
		} else {
			throw new TypeError('Unknown OTP type');
		}

		// Label: required
		// Issuer: optional
		if (uriLabel.length === 2) {
			config.label = uriLabel[1];
			if (typeof uriParams.issuer === 'undefined') {
				config.issuer = uriLabel[0];
			} else if (uriParams.issuer === uriLabel[0]) {
				config.issuer = uriParams.issuer;
			} else {
				throw new TypeError('Invalid \'issuer\' parameter');
			}
		} else {
			config.label = uriLabel[0];
			if (typeof uriParams.issuer !== 'undefined') {
				config.issuer = uriParams.issuer;
			}
		}

		// Secret: required
		if (typeof uriParams.secret !== 'undefined' && SECRET_REGEX.test(uriParams.secret)) {
			config.secret = new Secret({buffer: Utils.b32.encode(uriParams.secret)});
		} else {
			throw new TypeError('Missing or invalid \'secret\' parameter');
		}

		// Algorithm: optional
		if (typeof uriParams.algorithm !== 'undefined') {
			if (ALGORITHM_REGEX.test(uriParams.algorithm)) {
				config.algorithm = uriParams.algorithm;
			} else {
				throw new TypeError('Invalid \'algorithm\' parameter');
			}
		}

		// Digits: optional
		if (typeof uriParams.digits !== 'undefined') {
			if (POSITIVE_INTEGER_REGEX.test(uriParams.digits)) {
				config.digits = parseInt(uriParams.digits, 10);
			} else {
				throw new TypeError('Invalid \'digits\' parameter');
			}
		}

		return new OTP(config);
	}

	/**
	 * Converts an HOTP/TOTP object to a Google Authenticator key URI.
	 * @method stringify
	 * @param {Object} otp HOTP/TOTP object.
	 * @param {Object} [config] Configuration options.
	 * @param {boolean} [config.legacyIssuer=true] Set issuer label prefix.
	 * @returns {string} Google Authenticator Key URI.
	 */
	static stringify(otp, {legacyIssuer = true} = {}) {
		const isHOTP = otp instanceof HOTP;
		const isTOTP = otp instanceof TOTP;

		if (!isHOTP && !isTOTP) {
			throw new TypeError('Invalid \'HOTP/TOTP\' object');
		}

		// Key URI format:
		// otpauth://TYPE/[ISSUER:]LABEL?PARAMETERS
		let uri = 'otpauth://';

		// Type
		uri += (isTOTP ? 'totp' : 'hotp') + '/';

		// Label and optional issuer
		if (otp.issuer.length > 0) {
			// Issuer label prefix
			if (legacyIssuer) {
				uri += `${otp.issuer}:`;
			}

			uri += `${otp.label}?issuer=${otp.issuer}&`;
		} else {
			// No issuer
			uri += `${otp.label}?`;
		}

		// Generic parameters
		uri += `secret=${otp.secret.b32}` +
			`&algorithm=${otp.algorithm}` +
			`&digits=${otp.digits}`;

		// Extra parameters
		if (isTOTP) {
			// TOTP parameters
			uri += `&period=${otp.period}`;
		} else {
			// HOTP parameters
			uri += `&counter=${otp.counter}`;
		}

		return encodeURI(uri);
	}
}

/**
 * An object containing some utilities.
 * @type {Object}
 */
export const Utils = {};

/**
 * UInt conversion.
 * @type {Object}
 */
Utils.uint = {};

/**
 * Converts an ArrayBuffer to an integer.
 * @param {ArrayBuffer} buf ArrayBuffer.
 * @returns {number} Integer.
 */
Utils.uint.decode = function (buf) {
	const arr = new Uint8Array(buf);
	let num = 0;

	for (let i = 0; i < arr.length; i++) {
		if (arr[i] === 0) {
			continue;
		}

		num *= 256;
		num += arr[i];
	}

	return num;
};

/**
 * Converts an integer to an ArrayBuffer.
 * @param {number} num Integer.
 * @returns {ArrayBuffer} ArrayBuffer.
 */
Utils.uint.encode = function (num) {
	const buf = new ArrayBuffer(8);
	const arr = new Uint8Array(buf);
	let acc = num;

	for (let i = 7; i >= 0; i--) {
		if (acc === 0) {
			break;
		}

		arr[i] = acc & 255;
		acc -= arr[i];
		acc /= 256;
	}

	return buf;
};

/**
 * Raw string conversion.
 * @type {Object}
 */
Utils.raw = {};

/**
 * Converts an ArrayBuffer to a string.
 * @param {ArrayBuffer} buf ArrayBuffer.
 * @returns {string} String.
 */
Utils.raw.decode = function (buf) {
	const arr = new Uint8Array(buf);
	let str = '';

	for (let i = 0; i < arr.length; i++) {
		str += String.fromCharCode(arr[i]);
	}

	return str;
};

/**
 * Converts a string to an ArrayBuffer.
 * @param {string} str String.
 * @returns {ArrayBuffer} ArrayBuffer.
 */
Utils.raw.encode = function (str) {
	const buf = new ArrayBuffer(str.length);
	const arr = new Uint8Array(buf);

	for (let i = 0; i < str.length; i++) {
		arr[i] = str.charCodeAt(i);
	}

	return buf;
};

/**
 * Base32 string conversion.
 * @type {Object}
 */
Utils.b32 = {};

/**
 * RFC 4648 base32 alphabet without pad.
 * @type {string}
 */
Utils.b32.alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ234567';

/**
 * Converts an ArrayBuffer to a base32 string (RFC 4648).
 * @see https://github.com/LinusU/base32-decode
 * @param {ArrayBuffer} buf ArrayBuffer.
 * @returns {string} Base32 string.
 */
Utils.b32.decode = function (buf) {
	const arr = new Uint8Array(buf);

	let bits = 0;
	let value = 0;
	let str = '';

	for (let i = 0; i < arr.length; i++) {
		value = (value << 8) | arr[i];
		bits += 8;

		while (bits >= 5) {
			str += Utils.b32.alphabet[(value >>> bits - 5) & 31];
			bits -= 5;
		}
	}

	if (bits > 0) {
		str += Utils.b32.alphabet[(value << 5 - bits) & 31];
	}

	return str;
};

/**
 * Converts a base32 string to an ArrayBuffer (RFC 4648).
 * @see https://github.com/LinusU/base32-encode
 * @param {string} str Base32 string.
 * @returns {ArrayBuffer} ArrayBuffer.
 */
Utils.b32.encode = function (str) {
	const strUpp = str.toUpperCase();
	const buf = new ArrayBuffer(str.length * 5 / 8 | 0);
	const arr = new Uint8Array(buf);

	let bits = 0;
	let value = 0;
	let index = 0;

	for (let i = 0; i < strUpp.length; i++) {
		let idx = Utils.b32.alphabet.indexOf(strUpp[i]);

		if (idx === -1) {
			throw new TypeError('Invalid character found: ' + strUpp[i]);
		}

		value = (value << 5) | idx;
		bits += 5;

		if (bits >= 8) {
			arr[index++] = (value >>> bits - 8) & 255;
			bits -= 8;
		}
	}

	return buf;
};

/**
 * Hexadecimal string conversion.
 * @type {Object}
 */
Utils.hex = {};

/**
 * Converts an ArrayBuffer to a hexadecimal string.
 * @param {ArrayBuffer} buf ArrayBuffer.
 * @returns {string} Hexadecimal string.
 */
Utils.hex.decode = function (buf) {
	const arr = new Uint8Array(buf);
	let str = '';

	for (let i = 0; i < arr.length; i++) {
		const hexByte = arr[i].toString(16);

		str += hexByte.length === 1
			? '0' + hexByte
			: hexByte;
	}

	return str.toUpperCase();
};

/**
 * Converts a hexadecimal string to an ArrayBuffer.
 * @param {string} str Hexadecimal string.
 * @returns {ArrayBuffer} ArrayBuffer.
 */
Utils.hex.encode = function (str) {
	const buf = new ArrayBuffer(str.length / 2);
	const arr = new Uint8Array(buf);

	for (let i = 0; i < arr.length; i++) {
		arr[i] = parseInt(str.substr(i * 2, 2), 16);
	}

	return buf;
};

/**
 * An object containing some utilities (for internal use only).
 * @private
 * @type {Object}
 */
export const InternalUtils = {};

/**
 * Detect if running in "Node.js".
 * @type {boolean}
 */
// eslint-disable-next-line dot-notation
InternalUtils.isNode = Object.prototype.toString.call(global['process']) === '[object process]';

/**
 * Dynamically import "Node.js" modules.
 * @param {string} name Name.
 * @returns {Object} Module.
 */
InternalUtils.require = function (name) {
	// eslint-disable-next-line no-eval
	return InternalUtils.isNode ? eval('require')(name) : null;
};
